import React, { useEffect, useState } from "react";
import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import { FixedSizeList } from 'react-window';

function renderRow({data}) {

  return (
    <ListItem style={style} key={index} component="div" disablePadding>
      <ListItemButton>
        <ListItemText primary={`Item ${index + 1}`} />
      </ListItemButton>
    </ListItem>
  );
}

export default function InteractionPercentage({data}) {
  const [listData, setListData] = useState([]);

  const totalInteractions = data.length

  const getOccurance = (name) => {
    const ocurrances = data.filter((interaction) => interaction.name === name)
    if (ocurrances) return ocurrances.length
  }
  const getPercentage = (interaction) => {
    const amount = getOccurance(interaction)
    const frequency = totalInteractions * amount
    const percentage = frequency / 100
    return percentage
  }
  const populatePecentage = (items) => {
    return items.reduce((acc, item) => {
    const newItem = {...item}
    newItem.percentage = getPercentage(newItem.name)
    acc.push(newItem)
    return acc
    },[])
  }

  const removeDuplicate = (interactions) => {
    const filteredArr = interactions.reduce((acc, current) => {
      const x = acc.find(item => item.sector_id === current.sector_id);
      if (!x) {
        return acc.concat([current]);
      } else {
        return acc;
      }
    }, []);
    return filteredArr
    }

  useEffect(() => {
    if (data) {
      const newData = populatePecentage(data)
      const uniqueNewData = removeDuplicate(newData)
      setListData(uniqueNewData)
    }
  }, [data])

  console.log(listData)

  return (
    <Box
      sx={{ width: '100%', height: 400, maxWidth: 360, bgcolor: 'background.paper' }}
    >
      <FixedSizeList
        height={400}
        width={360}
        itemSize={41}
        itemCount={200}
        overscanCount={5}
      >
        {renderRow}
      </FixedSizeList>
    </Box>
  );
}
