import React, { useEffect, useState } from "react";
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import axios from 'axios';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: "#3d9970",
    textAlign: "center",
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
    textAlign: "center",
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

function createData(id, name, date) {
  return { id, name, date};
}

const rows = [
  createData(1,'Energy', "garri", 6.0, 24, 4.0),
  createData(2,'Healthcare', 237, 9.0, 37, 4.3),
  createData(3, 'Real Estate', 262, 16.0, 24, 6.0),
];


const InteractionTable = () => {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(false);

  useEffect(() => {
    setShowLoading(true)
    axios.get(`http://localhost:8080/interactions`)
    .then(res => {
      const interactions = res.data;
      setData(interactions);
      setShowLoading(false)
    })
  }, [])

  return (
    <div style={{padding:"10px 20px"}}>
      {showLoading && (
      <Box sx={{ display: 'flex' }}>
      <CircularProgress />
    </Box>)}
      {data.length > 0 &&
        <TableContainer component={Paper}>
            <Table  aria-label="customized table">
                <TableHead>
                    <TableRow>
                        <StyledTableCell>Sector ID</StyledTableCell>
                        <StyledTableCell align="right">Name</StyledTableCell>
                        <StyledTableCell align="right">Date</StyledTableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {data.map((row, i) => (
                        <StyledTableRow key={row.sector_i}>
                        <StyledTableCell component="th" scope="row">
                            {row.sector_id}
                        </StyledTableCell>
                        <StyledTableCell align="right">{row.name}</StyledTableCell>
                        <StyledTableCell align="right">{row.date}</StyledTableCell>
                        </StyledTableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>}
    </div>
  )
}

export default InteractionTable;
